package Networking;

import java.io.IOException;
import java.io.ObjectOutputStream;

/**
 * The client sender class. This relays any string s generated by the client to a server it's connected to.
 * @author djs568
 *
 */
public class ClientSender extends Thread {

	private ObjectOutputStream server;
	private Queue q;
	private boolean running;
	
	/**
	 * Constructor.
	 * @param server The PrintStream used to send messages to a server.
	 */
	public ClientSender(ObjectOutputStream server, Queue q) {
		this.server = server;
		this.q = q;
		running = false;
	}
	
	public void run() {
		running = true;
	
		while(running) {
			try {
				Object obj = q.take();
				server.writeObject(obj);
				server.flush();
				server.reset();
			}
			catch(InterruptedException e) {
				e.printStackTrace();
				System.exit(1);
			} 
			catch(IOException e) {
				close();
			}
		}
	}
	
	public void send(Object obj) {
		q.offer(obj);
	}
		
	private void close() {
		running = false;
		//System.out.println(getName() + " closed.");
	}
}
