package Networking;

import java.io.IOException;
import java.io.ObjectOutputStream;
import java.io.PrintStream;
import java.util.ArrayList;

import GameLogic.Move;
import GameLogic.UserInput;

/**
 * The client sender class. This relays any string s generated by the client to a server it's connected to.
 * @author djs568
 *
 */
public class ClientSender extends Thread {

	private ObjectOutputStream server;
	private MoveQueue q;
	private boolean running;
	private String name;
	
	/**
	 * Constructor.
	 * @param server The PrintStream used to send messages to a server.
	 */
	public ClientSender(ObjectOutputStream server, MoveQueue q, String name) {
		this.server = server;
		this.q = q;
		this.name = name;
	}
	
	/**
	 * Run method of the thread. Reads an ArrayList of strings from from the client 25 times a second.
	 */
	public void run() {
		running = true;
		
		try {
			while(running) {
				Move move = q.getMove(); 
				//String nextInput = input.getInputStrings(); // I'm pretty sure we're safer having a q here so we don't get odd  null points.
				//for(String s: nextInputs) {
				//	server.print(s);	
				//}
				server.writeObject(move);
				server.flush();
				
				sleep(40);
			}
			
			server.close();
		}
		catch(InterruptedException e) {
			e.printStackTrace();
			System.exit(1);
		} catch (IOException e) {
			e.printStackTrace();
			System.exit(1);
		}
	}
	
	public void close() {
		running = false;
	}
}
